import 'package:flutter/material.dart';class InputSearchWidget extends StatefulWidget {  final String? hintText;  final TextInputType? inputType;  final TextEditingController? controller;  final FocusNode? fieldFocusNode;  final ValueChanged<String>? onValueChanged;  final Icon? icon;  final bool isLoading;  final int maxLines;  final TextStyle? textStyle;  const InputSearchWidget({    Key? key,    this.isLoading = false,    this.hintText,    this.inputType,    this.controller,    this.maxLines = 1,    this.onValueChanged,    this.icon,    this.fieldFocusNode,    this.textStyle,  }) : super(key: key);  @override  _InputSearchWidget createState() => _InputSearchWidget();}class _InputSearchWidget extends State<InputSearchWidget> {  @override  Widget build(BuildContext context) {    return Card(        child: TextField(      onChanged: widget.onValueChanged,      focusNode: widget.fieldFocusNode,      controller: widget.controller,      keyboardType: widget.inputType,      maxLines: widget.maxLines,      textInputAction: TextInputAction.done,      textAlign: TextAlign.justify,      style: widget.textStyle ?? const TextStyle(fontSize: 18.0, color: Colors.blueGrey, fontWeight: FontWeight.bold),      decoration: InputDecoration(        enabled: true,        filled: true,        fillColor: const Color(0xFFe5e5e5),        border: InputBorder.none,        hintText: widget.hintText,        prefixIcon: widget.icon,        suffix: widget.isLoading            ? const SizedBox(                height: 15.0,                width: 15.0,                child: CircularProgressIndicator(strokeWidth: 2.0,),              )            : null,      ),    ));  }}