import 'package:weatherdemo/core/fetch_locations/data/fetch_locations_repository.dart';import 'package:weatherdemo/core/fetch_locations/domain/models_domain/locations_domain.dart';class FetchLocationsUsesCase {  final FetchLocationsRepository _fetchLocationsRepository;  FetchLocationsUsesCase(this._fetchLocationsRepository);  Future<List<LocationDomain>>  invoke(String search) async {      if(search.length < 3 ){        return [];      }      final response = await _fetchLocationsRepository.fetchLocations(search);      if(response.left != null){        throw response.left!;      }      return response.right ?? [];  }}