import 'dart:convert' as convert;import 'current.dart';import 'daily.dart';class Weather {  double? lat;  double? lon;  String? timezone;  int? timezoneOffset;  Current? current;  List<Daily>? daily;  Weather(this.lat, this.lon, this.timezone, this.timezoneOffset, this.current, this.daily);  Weather.fromJson(dynamic json) {    lat = json['lat'];    lon = json['lon'];    timezone = json['timezone'];    timezoneOffset = json['timezone_offset'];    current = json['current'] != null ? Current.fromJson(json['current']) : null;    if (json['daily'] != null) {      daily = [];      json['daily'].forEach((v) {        daily?.add(Daily.fromJson(v));      });    }  }}