import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:weatherdemo/core/utils/commons/dimens_app.dart';Widget emptyState(String imageEmpty, String message) {  return Container(    color:  const Color(0xb5f2f3f6),    padding: const EdgeInsets.symmetric(horizontal: 16.0),    child: Column(      children: [        Image.network(          imageEmpty,          fit: BoxFit.fill,          width: emptyScreenImageWidth,          height: emptyScreenImageHeight,        ),        const SizedBox(          height: 16.0,        ),        Text(message,            textAlign: TextAlign.center, style: const TextStyle(fontSize: 18.0, fontStyle: FontStyle.italic, fontWeight: FontWeight.w500))      ],    ),  );}