import 'package:weatherdemo/feature/home_weather/data/fetch_weather_repository.dart';import 'package:weatherdemo/feature/home_weather/domain/models_domain/weather_domain.dart';class FetchWeatherUsesCase {  final FetchWeatherRepository _fetchWeatherRepository;  FetchWeatherUsesCase(this._fetchWeatherRepository);  Future<WeatherDomain> invoke(String latitude, String longitude) async {    if(latitude.isEmpty || longitude.isEmpty){      return WeatherDomain(null, []);    }    final response = await _fetchWeatherRepository.fetchWeather(latitude, longitude);    if (response.left != null) {      throw response.left!;    }    return response.right!;  }}